import React from 'react';
import classes from './MainPage.module.css';
const MainPage =(props)=>{
    let statistic = [
        {
            origin: 'Customer', 
            number: 36254, 
            numpercent: false,
            unit: '', 
            procent: 5.27, 
            increase: true, 
            time: 'Since last month'
        }, 
        {
            origin: 'Customer1', 
            number: 36254, 
            numpercent: false,
            unit: '', 
            procent: 5.27, 
            increase: true, 
            time: 'Since last month'
        },
        {
            origin: 'Revenue', 
            number: 6254,
            numpercent: false, 
            unit: '$', 
            procent: 7.00, 
            increase: false, 
            time: 'Since last month'
        },
        {
            origin: 'Growth', 
            number: 30.56,
            numpercent: true ,
            unit: '+', 
            procent: 4.87, 
            increase: true, 
            time: 'Since last month'
        }
    ];
    
    return(
        <div className = {classes.MainPage}>
            <div className = {classes.UzuuleltList}>
                {statistic.map(igkey =>{
                    return(
                        <div className = {classes.Uzuulelt}>
                            <h4>{igkey.origin}</h4>
                            <h5>{igkey.unit}{igkey.number}{igkey.numpercent? '%': ''}</h5>
                            <p> <span className= {igkey.increase? classes.green: classes.red}> {igkey.increase ?<i class="mdi mdi-arrow-up-bold"></i> : <i class="mdi mdi-arrow-down-bold"></i>}{igkey.procent.toFixed(2)}%</span>{igkey.time}</p>
                        </div>
                    );
                })}
               
            </div>
        </div>
    );
}
export default MainPage;
