import React from 'react';
import classes from './Vehicle.module.css';
class Vehicle extends React.Component{
    state = {
        selectedTab: 1, 
        vehicleForm: {
            countryNumber:{
                elementType: 'input', 
                elementConfig: {
                    type: 'text', 
                    placeholder: 'Улсын дугаар'
                }, 
                value: '', 
                validation: {
                    required: true, 
                    minLength: 5, 
                    maxLength: 5
                }, 
                valid: false, 
                touched: false
                }, 
            arliinNumber: {
                elementType: 'input', 
                elementConfig: {
                    type: 'text', 
                    placeholder: 'Арлын дугаар'
                }, 
                value: '', 
                validation: {
                    required: true, 
                    minLength: 5, 
                    maxLength: 5
                }, 
                valid: false, 
                touched: false
            }, 
            Manufacturer: {
                elementType: 'input', 
                elementConfig: {
                    type: 'text', 
                    placeholder: 'Manufacturer'
                }, 
                value: '', 
                validation: {
                    required: true, 
                    minLength: 5, 
                    maxLength: 5
                }, 
                valid: false, 
                touched: false
            },
            model: {
                elementType: 'input', 
                elementConfig: {
                    type: 'text', 
                    placeholder: 'model'
                }, 
                value: '', 
                validation: {
                    required: true, 
                    minLength: 5, 
                    maxLength: 5
                }, 
                valid: false, 
                touched: false
            }, 
            variant: {
                elementType: 'input', 
                elementConfig: {
                    type: 'text', 
                    placeholder: 'variant'
                }, 
                value: '', 
                validation: {
                    required: true, 
                    minLength: 5, 
                    maxLength: 5
                }, 
                valid: false, 
                touched: false
            }, 
            regYear: {
                elementType: 'input', 
                elementConfig: {
                    type: 'text', 
                    placeholder: 'regYear'
                }, 
                value: '', 
                validation: {
                    required: true, 
                    minLength: 4, 
                    maxLength: 4, 
                    isNumeric: true
                }, 
                valid: false, 
                touched: false
            }, 
            mnRegYear: {
                elementType: 'input', 
                elementConfig: {
                    type: 'text', 
                    placeholder: 'mnRegYear'
                }, 
                value: '', 
                validation: {
                    required: true, 
                    minLength: 4, 
                    maxLength: 4, 
                    isNumeric: true
                }, 
                valid: false, 
                touched: false
            }
        }, 
        userForm: {
            surname: {
                elementType: 'input', 
                elementConfig: {
                    type: 'text', 
                    placeholder: 'surname'
                }, 
                value: '', 
                validation: {
                    required: true, 
                    minLength: 1, 
                    maxLength: 20
                }, 
                valid: false, 
                touched: false
            }, 
            firstname: {
                elementType: 'input', 
                elementConfig: {
                    type: 'text', 
                    placeholder: 'firstname'
                }, 
                value: '', 
                validation: {
                    required: true, 
                    minLength: 1, 
                    maxLength: 20
                }, 
                valid: false, 
                touched: false
            }, 
            registrationNumber: {
                elementType: 'input', 
                elementConfig: {
                    type: 'text', 
                    placeholder: 'registrationNumber'
                }, 
                value: '', 
                validation: {
                    required: true, 
                    minLength: 5, 
                    maxLength: 5, 
                    isRegistrationNumber: true
                }, 
                valid: false, 
                touched: false
            }
        }
    };
    listHandler =(num)=>{
        this.setState({selectedTab: num});
    }
    render(){
        let loadingPosition = this.state.selectedTab===1 ? '40%' : this.state.selectedTab ===2 ? '70%': '100%'; 
    return(
        <div className = {classes.Vehicle}>
            <div className = {classes.Tab}>
                <ul>
                    <li onClick = {()=>this.listHandler(1)} className = {this.state.selectedTab === 1 ? classes.SelectedTab: classes.UnselectedTab}>
                        <i class="mdi mdi-account-circle mr-1"></i>
                        <div className = {classes.TabText}>
                            Тээврийн хэрэгслийн мэдээлэл
                        </div>

                    </li>
                    <li onClick = {()=>this.listHandler(2)} className = {this.state.selectedTab === 2 ? classes.SelectedTab: classes.UnselectedTab}>
                        <i class="mdi mdi-face-profile mr-1"></i>
                        <div className = {classes.TabText}>
                            Даатгуулагчийн мэдээлэл
                        </div>
                    </li>
                    <li onClick = {()=>this.listHandler(3)} className = {this.state.selectedTab === 3 ? classes.SelectedTab: classes.UnselectedTab}>
                        <i class="mdi mdi-checkbox-marked-circle-outline mr-1"></i>
                        <div className = {classes.TabText}>
                            Ерөнхий мэдээлэл
                        </div>
                    </li>
                </ul>
                <div className = {classes.VehicleContainer}>
                    
                    
                    <div className={[classes.lines, classes.stripesLoader].join(' ')} style={{backgroundPosition: loadingPosition}}>
                    </div>

                </div>
                <form action="">
                    <div className = {classes.formGroup}>
                        <label htmlFor="">Улсын дугаар</label>
                        <input type="text" placeholder = "Улсын дугаар" id = "countryNumber" name = "countryNumber" value = ""/>
                        <i className = "mdi mdi-check"></i>
                    </div>
                </form>
                <div className = {classes.PrevNextContainer}>
                <button className= {classes.Prev}>Prev</button>
                <button className = {classes.Next}>Next</button>
            </div>
            </div>
            
        </div>
    );
    }
}
export default Vehicle;
