import React from 'react';
import classes from './Vehicle.module.css';
class Vehicle extends React.Component{
    state = {
        selectedTab: 1, 
        vehicleForm: {
            countryNumber:{
                elementType: 'input', 
                elementConfig: {
                    type: 'text', 
                    placeholder: 'Улсын дугаар'
                }, 
                value: '', 
                validation: {
                    required: true, 
                    minLength: 5, 
                    maxLength: 5
                }, 
                valid: false, 
                touched: false
                }, 
            arliinNumber: {
                elementType: 'input', 
                elementConfig: {
                    type: 'text', 
                    placeholder: 'Арлын дугаар'
                }, 
                value: '', 
                validation: {
                    required: true, 
                    minLength: 5, 
                    maxLength: 5
                }, 
                valid: false, 
                touched: false
            }, 
            Manufacturer: {
                elementType: 'input', 
                elementConfig: {
                    type: 'text', 
                    placeholder: 'Manufacturer'
                }, 
                value: '', 
                validation: {
                    required: true, 
                    minLength: 5, 
                    maxLength: 5
                }, 
                valid: false, 
                touched: false
            },
            model: {
                elementType: 'input', 
                elementConfig: {
                    type: 'text', 
                    placeholder: 'Model'
                }, 
                value: '', 
                validation: {
                    required: true, 
                    minLength: 5, 
                    maxLength: 5
                }, 
                valid: false, 
                touched: false
            }, 
            variant: {
                elementType: 'input', 
                elementConfig: {
                    type: 'text', 
                    placeholder: 'Variant'
                }, 
                value: '', 
                validation: {
                    required: true, 
                    minLength: 5, 
                    maxLength: 5
                }, 
                valid: false, 
                touched: false
            }, 
            regYear: {
                elementType: 'input', 
                elementConfig: {
                    type: 'text', 
                    placeholder: 'Reg Year'
                }, 
                value: '', 
                validation: {
                    required: true, 
                    minLength: 4, 
                    maxLength: 4, 
                    isNumeric: true
                }, 
                valid: false, 
                touched: false
            }, 
            mnRegYear: {
                elementType: 'input', 
                elementConfig: {
                    type: 'text', 
                    placeholder: 'Mn Reg Year'
                }, 
                value: '', 
                validation: {
                    required: true, 
                    minLength: 4, 
                    maxLength: 4, 
                    isNumeric: true
                }, 
                valid: false, 
                touched: false
            }
        }, 
        userForm: {
            surname: {
                elementType: 'input', 
                elementConfig: {
                    type: 'text', 
                    placeholder: 'Овог'
                }, 
                value: '', 
                validation: {
                    required: true, 
                    minLength: 1, 
                    maxLength: 20
                }, 
                valid: false, 
                touched: false
            }, 
            firstname: {
                elementType: 'input', 
                elementConfig: {
                    type: 'text', 
                    placeholder: 'Нэр'
                }, 
                value: '', 
                validation: {
                    required: true, 
                    minLength: 1, 
                    maxLength: 20
                }, 
                valid: false, 
                touched: false
            }, 
            registrationNumber: {
                elementType: 'input', 
                elementConfig: {
                    type: 'text', 
                    placeholder: 'Регистерийн дугаар'
                }, 
                value: '', 
                validation: {
                    required: true, 
                    minLength: 5, 
                    maxLength: 5, 
                    isRegistrationNumber: true
                }, 
                valid: false, 
                touched: false
            }, 
            phoneNumber: {
                elementType: 'input', 
                elementConfig: {
                    type: 'text', 
                    placeholder: 'Утасны дугаар'
                }, 
                value: '', 
                validation: {
                    required: true, 
                    minLength: 8, 
                    maxLength: 8, 
                    isNumeric: true
                }, 
                valid: false, 
                touched: false
            }, 
            email: {
                elementType: 'input', 
                elementConfig: {
                    type: 'text', 
                    placeholder: 'Имэйл хаяг'
                }, 
                value: '', 
                validation: {
                    required: true, 

                    isEmail: true
                }, 
                valid: false, 
                touched: false
            }
        }, 
        generalForm: {
            agreeCheck: {
                elementType: 'input', 
                elementConfig: {
                    type: 'checkbox', 
                    checked: false
                }, 
                value: '', 
                validation: {
                    required: true, 
                    isChecked: true
                }, 
                valid: false, 
                touched: false
            }
        }
    };

    listHandler =(num)=>{
        this.setState({selectedTab: num});
       
    }
    render(){
        let loadingPosition = this.state.selectedTab===1 ? '40%' : this.state.selectedTab ===2 ? '70%': '100%'; 
        let formElements = null;
        if(this.state.selectedTab ===1){
            formElements = {...this.state.vehicleForm};
            
        }else if(this.state.selectedTab ===2){
            formElements = {...this.state.userForm};
        }else {
            formElements = {...this.state.generalForm};
        }
        const elementFormList = [];
        for(let i in formElements){
            elementFormList.push(
               { id: i, config: formElements[i]}
            );
        }
        
        const elementTemp = elementFormList.map(igkey=>{
                return(
                    <div className = {classes.formElement}>
                        <label htmlFor="">{igkey.config.elementConfig.placeholder}</label>
                    </div>    
                );
            })
        
        
    return(
        <div className = {classes.Vehicle}>
            <div className = {classes.Tab}>
                <ul>
                    <li onClick = {()=>this.listHandler(1)} className = {this.state.selectedTab === 1 ? classes.SelectedTab: classes.UnselectedTab}>
                        <i className="mdi mdi-account-circle mr-1"></i>
                        <div className = {classes.TabText}>
                            Тээврийн хэрэгслийн мэдээлэл
                        </div>

                    </li>
                    <li onClick = {()=>this.listHandler(2)} className = {this.state.selectedTab === 2 ? classes.SelectedTab: classes.UnselectedTab}>
                        <i className="mdi mdi-face-profile mr-1"></i>
                        <div className = {classes.TabText}>
                            Даатгуулагчийн мэдээлэл
                        </div>
                    </li>
                    <li onClick = {()=>this.listHandler(3)} className = {this.state.selectedTab === 3 ? classes.SelectedTab: classes.UnselectedTab}>
                        <i className="mdi mdi-checkbox-marked-circle-outline mr-1"></i>
                        <div className = {classes.TabText}>
                            Ерөнхий мэдээлэл
                        </div>
                    </li>
                </ul>
                <div className = {classes.VehicleContainer}> 
                    <div className={[classes.lines, classes.stripesLoader].join(' ')} style={{backgroundPosition: loadingPosition}}>
                    </div>

                </div>
                <form action="">
                    
                    
                </form>
                <div className = {classes.PrevNextContainer}>
                <button className= {classes.Prev}>Prev</button>
                <button className = {classes.Next}>Next</button>
            </div>
            </div>
            
        </div>
    );
    }
}
export default Vehicle;
